custom-plp.scss:
@import "variable";

.breadcrumbs{text-align: $align-left; margin-top:30px; margin-bottom:5px; 
 .breadcrumb{font-size: 14px; color:$color-gray80; display:inline;
  a{color:$color-gray80;
   &:hover{color:$color-brown;}
  }
 }
}

h1.page-heading{
 margin-top: 20px !important;
 @include breakpoint("small") {margin-top: 0px !important;}
}

.category-desc{width: $full-width; text-align: $align-center; display:inline-block; vertical-align: top; margin-bottom:8px;
 @include breakpoint("large") {margin-bottom:24px;}
 @include breakpoint("small") {margin-bottom:14px;}
 
 h1{margin-top:20px !important;
    @include breakpoint("small") {margin-top:0 !important;} 
 }
 
 img{width: 100%;}
}

.mainlisting{
	#faceted-search-container{
	 nav{
	  .mln{display:none;}
	  .sidebarBlock + .sidebarBlock{border-top: none;margin-top:0;padding-top: 0;}	  
	 }
	}
}

#faceted-search-container{ font-size:$base-font-size; color:$text-color-standard-gray;
 .facetedSearch-toggle{border-color:$color-gray80; font-size: 14px; padding: 0.75rem 1rem; color:$text-color-standard-gray;
	@include breakpoint("small") {max-width:$full-width; margin-bottom:34px;}
	
    &.is-open{
	 @include breakpoint("small") {margin-bottom:0;}
	}
	
 .facetedSearch-toggle-text{ width: 85.333%;
	@include breakpoint("small") {width: 58.333%;}
 }
 
.facetedSearch-toggle-indicator{width: 14.66667%;
 @include breakpoint("small") {width: 41.66667%;}
 }
}

.accordion{border-color:$color-gray80; }

.accordion-navigation{
 @include breakpoint("large") {border-bottom:$color-gray80 solid 1px;  margin-bottom: 5px !important;}
 border-color:$color-gray80;

&.is-open{border-bottom-color:transparent;
  @include breakpoint("large") {border-color:$color-gray80 !important;}
  h5{padding-bottom: 0;}
}

.accordion-navigation-actions{top: 0.5rem;
 @include breakpoint("large") {top: 0;}
 
  .accordion-indicator{
   &.toggleLink-text--off{background-image: $select-backgroundImage; background-position: right center; background-size: 18px; background-repeat: no-repeat;-webkit-transform: rotate(-90deg); transform: rotate(-90deg);
    use{display:none;}
   }
   
   &.toggleLink-text--on{background-image: $select-backgroundImage; background-position: right center; background-size: 18px; background-repeat: no-repeat;
    use{display:none;}
   }
  }
 }
}

h5{font-size:16px; font-family: $font-arial; color:$text-color-standard-gray; font-weight:$font-weight-bold; padding: 0.75rem 1rem;
 @include breakpoint("large") {font-size:18px; margin-bottom: 5px; padding: 0;}
}
 
 
.navList-action--checkbox{font-size:$base-font-size;  color:$text-color-standard-gray;
  &::before{border-radius: 0; border-color: $color-gray80;}
}
 
#facetedSearch-content--price{
  @include breakpoint("large") {margin-top:10px;}
}

.form-input{height:44px; border-color:$text-color-standard-gray; color:$text-color-standard-gray; font-size:15px; font-family:$font-arial;}
.button{height:44px; border-color:$text-color-standard-gray; color:$text-color-standard-gray; font-size:15px;
 &:hover{background: $text-color-standard-gray; border-color: $text-color-standard-gray; color: $color-white;}
}
 
}



#product-listing-container{
 .plp-head-m{width: $full-width; display:inline-block; vertical-align: top; margin-bottom:24px;}
 .actionBar{margin:0;}
}

.cat_total{font-size: $base-font-size; color: $text-color-standard-gray; width:$full-width; text-align:$align-center;
 @include breakpoint("small") {width:auto; float:right; text-align: $align-left;}
 
  p{margin:0 0 15px 0;
   @include breakpoint("small") {margin: 10px 0 0 0;}
  }
}

.wholesale-login-button{width: $full-width; display:inline-block; vertical-align: top; margin-bottom:1.5rem;
 a{ @extend %blue-button; width: $full-width; margin:0 !important;
  &:hover{@extend %blue-button-hover;}
  &:focus{@extend %blue-button-hover;}
  &:visited{@extend %blue-button;}
 }
}

.actionBar-section{
  .form-field{margin-bottom:27px; border-color: $color-gray80; width: $full-width; position:relative; box-sizing: border-box; overflow: visible;
   &:hover{border-color: $color-gray80;}
   @include breakpoint("large") {margin-bottom:16px;}
   @include breakpoint("small") {width: 240px;}
 
  .form-label{color:$text-color-standard-gray; font-size:1rem; font-weight:$font-weight-bold; position: absolute; top: 7px;}
  .form-select{width:$full-width;max-width:$full-width; color:$text-color-standard-gray; font-size:1rem; height:42px; line-height:1.2; padding:0 3.3rem 0 70px; background-position: right 1rem top 1rem; 
	box-sizing: border-box;
   @include breakpoint("large") {height:44px;}
   @include breakpoint("small") {width: $full-width;max-width: $full-width;}
   option[selected]{background: $color-gray80; color: $color-white;}
  }
 }
}

.new-flag-badge{position:relative; width:auto; float:left;
 .new-flag-badge-text{background:$color-gray80; padding:4px 10px; color:$color-white; text-transform:uppercase; border-radius:5px; position:absolute; top:5px; left:5px; z-index:9; font-size:14px; font-weight:$font-weight-bold;
  @include breakpoint("small") {padding:5px 13px; top:10px; left:10px;}
 }
}

.card-figcaption-body{transform: translate3d(0, -30px, 0); top: 100%;  width:$full-width;
 a.card-figcaption-button{
  &.quickview{background:rgba(238,238,238,0.9); display:block; border-radius:0; color:$text-color-standard-gray; font-size:14px; padding: 0.7139rem 1.5rem;    text-transform: capitalize;   font-family: $font-arial;
   &:hover{background:rgba(136,136,136,0.9); color:$color-white;}
  }
 }
}

.card-body{text-align:$align-center;

 .yotpo.bottomLine.yotpo-small{
  .yotpo-icon-star{font-size:16px; margin:0 2px;}
  a.text-m{display:none;}
  
 }

 .prd-sku p{font-size:14px; margin-bottom:0;}

 h4.card-title{text-align:$align-center; color:$text-color-standard-gray; font-size:16px; line-height:24px; font-weight:$font-weight-bold; font-family: Arial, Helvetica, sans-serif;
  a{color:$text-color-standard-gray;}
}

.price-section{font-size:16px; color: $text-color-standard-gray; display: inline-block; padding: 0 2px;
 &.rrp-price--withoutTax{display:none;}
 &.normal-price{
  .price{color:$text-color-standard-gray !important;}
 }
 .price{
   &.price--withoutTax{color:$color-red;}
 }
}

.ajax-ship{
 p{font-size:14px; color:$color-gray80; font-style:$font-style-italic; margin-bottom:0;}
}

}

.pagination{text-align:$align-center;
 ul.pagination-list{display:inline-block;
  li{font-size:16px; border:none; 
   &.pagination-item--current{background-color: $color-gray80; 
    a{color:$color-white;}
   }
  
   a{color:$text-color-standard-gray; padding: 0.38571rem 0.7rem;}
    &.pagination-item--next{
    a{color:$color-gray80;}  
   }
   
   &.pagination-item--previous{display:inline-block; vertical-align: top; float:none;
    a{color:$color-gray80;}
   }
   
  }
 }
}

.category-banner-bottom{padding-left: 0.75rem;padding-right: 0.75rem;width: $full-width;float: right;
 @include breakpoint("large") {width: 75%;}

 .banners{background-color: transparent; padding:0; text-align: $align-left;
  p{color:$text-color-standard-gray; margin-bottom: 16px; font-size:16px;}
 }
}



// Quick View [START]
#modal{
	.product-desc-top, .artist-div, .consumer-div, .addthis_toolbox, .productView-description, .rollover_img, .productView-thumbnails, .mobile-hide, .related-trending, .related-out-trending, .yotpo-testimonial-div, .product-blog, .yotpo-main-reviews, .yotpo-pictures-widget, .yotpo-btns-container a.text-m, .yotpo-btns-container .QABottomLine {display:none;}
	.quickview-detail{display:block;}
	.modal-close{font-size:0; background: url(../img/modal-close.png) no-repeat center center; height: 3.286rem; width: 3.286rem; }
	
}
// Quick View [CLOSE]



// Blog Page [START]

.page-blog{

.page{width:$full-width; max-width:$full-width; margin:0;}

.blog-banner{width:$full-width; display:inline-block; vertical-align:top; text-align:$align-center; margin-top:5px; margin-bottom:10px;
 figure{width:$full-width; text-align:$align-center; display:inline-block; vertical-align:top; margin:0;
  img{width:$full-width;}
 }
 h2{margin-top: 16px;}
}

.blog-grid-view{ vertical-align:top; display: flex; flex-wrap: wrap; margin:0 0 0 -3%; justify-content: left;
	
  @include breakpoint("small") {margin:0 0 0 -4%;}
  @include breakpoint("large") {margin:0 0 0 -3%;}
 
 .blog{width: $full-width; display:inline-block; vertical-align:top; padding:0; margin:0 0 27px 3%; border:none; background:#eeeeee;
  
  @include breakpoint("small") {width: 46%; margin:0 0 5% 4%;}
  @include breakpoint("large") {width: 30.333%; margin:0 0 5% 3%;}
  
  
 
  .blog-post-figure{width:$full-width;
   figure{margin:0; display:table; table-layout: fixed; max-width:$full-width; width:$full-width;
    a{display:table-cell; vertical-align:middle; text-align:$align-center; width:$full-width; max-height:215px; height:100%;
	 @include breakpoint("small") {max-height:265px; height:100%;}
     @include breakpoint("large") {height:301px;}
	
     img{max-height:100%; width:auto;
	  @include breakpoint("small") {max-height:100%; width:auto;}
      @include breakpoint("large") {max-height:301px; width:auto;}
	 }
	}
   }
  }
  .blog-post-body{width:$full-width; padding:20px; float:left;
   h2.blog-title{margin:0 0 8px 0; text-align: $align-left; font-size: 26px; line-height:30px;
    
	@include breakpoint("small") {font-size: 28px; line-height:32px;}
	
	a{color:$text-color-standard-gray;}
   }
   p.blog-date{@include paragraph; text-align: $align-left; margin-bottom:0; color:$color-gray80;}
  .blog-post{width:$full-width; float:left;}
  
  .blog-readmore{width:$full-width; float:left;
   a{color:#547689;
    &:hover{color:$color-gray80;}
   }
  }
  
  }
 }
}

.pagination{margin-top:18px;
 @include breakpoint("small") {margin-top:25px;}
 @include breakpoint("large") {margin-top:0;}
}

}

.page-blog_post{
 .blog{max-width:600px; margin:0 auto 1.5rem auto;
  .blog-post-header{
   h1.blog-title{font-size:32px; margin-left:0; margin-right:0; margin-top:20px !important; text-align:$align-center;
	@include breakpoint("small") {font-size:40px; margin-top:0px !important;}
   }
   p.blog-date{@include paragraph; color:$color-gray80;}
  }
  .blog-post-figure{width:$full-width;}
  .blog-post-body{width:$full-width;}
  .tags{margin-top: 7rem;
   .tag{font-size:16px;
    a{color:$color-gray80;
	 &:hover{color:$color-blue;}
	}
   }
  }
  
  .addthis_toolbox{ text-align:$align-center; margin-top: 2rem;
   .socialLinks li{display:inline-block; float:none;}
  } 
  
 }
}

.tag-page{width:$full-width; text-align:$align-center; margin-bottom:27px;display:none;
 @include breakpoint("medium") {margin-bottom:40px;}
 h2{text-align:$align-center; margin-top:15px;
  @include breakpoint("medium") {margin-top: 36px;}
 }
}


.article-by-mail{width:$full-width; float:left; text-align:$align-center; margin-top: 20px; margin-bottom: 25px;
 .article-by-mail-in{display:inline-block; vertical-align:top; max-width:600px; background:#eeeeee; width: $full-width; padding: 20px 14px 30px 14px;
  .abm-form-head{width:$full-width; float:left;
	h4{color:#29546b; text-align:$align-center; font-size:17px; margin:0 0 6px 0; font-weight:$font-weight-bold; font-family:$font-arial;}
	p{text-align:$align-center; font-size:15px; margin-bottom:13px;}
  }
  
  .abm-form{width:$full-width; float:left;
   .abm-form-left{width:$full-width; max-width:$full-width; display:inline-block; vertical-align:top; margin-right:0;  margin-bottom:13px;
    @include breakpoint("medium") {max-width:267px; margin-right:3px; margin-bottom:0;}
   }
   .abm-form-right{width:$full-width; max-width:$full-width; display:inline-block; vertical-align:top; margin-left:0;
     @include breakpoint("medium") {max-width:135px; margin-left:3px;}
	 
    .gray-button{@include gray-button; width:$full-width; float:left; line-height: 1; font-size:15px; margin-bottom:0 !important;}
   }
  }
  
 }
}

// Blog Page [CLOSE]




// Credit Application [START]

.page-content--credit-app{width:100%; max-width:930px; margin: 0 auto 40px auto; padding: 0; float: none;
  @include breakpoint("medium") {margin: 0 auto 45px auto;}
  @include breakpoint("small") {margin: 0 auto 35px auto;}

  h1.page-heading{margin-top:55px !important;
   @include breakpoint("small") {margin-top:30px !important;} 
  }
 .page-content{padding:0;}
}


.tooltip {position: relative; display: inline-block; vertical-align:middle; cursor:pointer;
 .tooltiptext {visibility: hidden; width:270px; background-color: $color-white; border: $color-gray80 solid 1px; color: $text-color-standard-gray; text-align: $align-left; border-radius: 5px; padding: 10px 15px; position: absolute; z-index: 1; bottom: 100%; left: 50%; margin-left: -135px;}
 
 &:hover{
  .tooltiptext {visibility: visible;}
 }
}



ul.stepper{margin:0; list-style: none !important; overflow-y: inherit !important; overflow-x: inherit !important;


.form-label{font-size: 14px; font-weight: 400;
 small{color: $color-red; text-transform: lowercase; font-weight: 600; font-size: 12px;}
}

.form-input{background-color: $color-white; font-size: 15px; color: $text-color-standard-gray !important;}

.error{width:$full-width; float:left; font-size:14px; color: $color-red; text-align: $align-left; margin-top:5px;}

.step{width: $full-width; float: left;

&::before{height: 34px !important;width: 34px !important;line-height: 34px !important;font-size: 24px !important; top: 24px !important; left: 8px !important; font-family: $font-palatino;
 
 @include breakpoint("small") {top: 20px !important;}
 
}

&.wrong{
  &::before{background-color: $color-red !important; content: '' !important; font-family: inherit !important; background-image: url(../img/warning-icon.png); background-repeat: no-repeat; background-position: center center;}
 }
 
&.done{
 &::before{background-color: $color-green !important; content: '' !important; font-family: inherit !important; background-image: url(../img/tick-icon.png); background-repeat: no-repeat; background-position: center center; top: 24px !important;
 
 @include breakpoint("small") {top: 27px !important;}
 
 }
 
 .step-title{
  span{margin-top:0px;
   
   @include breakpoint("small") {margin-top:9px;}  
   
}
  a{
   &.edit-button{display:block; float:right; font-size:0; background: url(../img/edit-icon.png) no-repeat center center; width:24px; height:52px;
    @include breakpoint("small") {display:block; float:right; @include white-button; width:auto; height:auto;}
   }
  }
 }
 
}

&.active{
 &::before{background-color: $color-gray80 !important;}
 &.wrong{
  &::before{background-color: $color-red !important;}
 }
 
} 

.step-title{width: $full-width; float: left; border-bottom: $color-gray80 solid 1px; padding: 15px 0px 15px 58px !important; margin:0 !important; line-height:1;

	@include breakpoint("small") {padding: 20px 0px 20px 58px !important;}
	
a{
 &.edit-button{display:none;}
}

&:hover{background-color: transparent !important;}
span{display:inline-block; vertical-align:top; max-width: 178px;
  @include breakpoint("small") { max-width: inherit;}
}

}

 &.trcls{
  .step-content{padding-top:35px !important;}
 }
 
 &.bicls{
 .step-content{padding-top:25px !important;
  p{margin-bottom:25px;}
 }
 }
 
 &.cocls{
 .step-content{padding-top:30px !important;}
 }
 
 &.cta_cls{
  .step-content{padding-top:35px !important;
   h3{text-align:$align-center; margin-top:0; font-size:26px; margin-bottom:25px;}
   p{margin-bottom:25px;}
  }
 
&.active{
 .step-content{border-bottom: none;}
} 
  
 }
	
.step-content{width: $full-width !important; float: left;margin-left: 0 !important; margin-right: 0 !important; padding: 45px 0px 45px 0px !important; border-bottom: #888888 solid 1px;
 
 .row{margin-bottom: 0 !important; font-size:0;
   @include breakpoint("small") {margin-left: -4%; }
  .col{
   &.s6{width: 100%; float: none; display:inline-block; vertical-align: top; margin: 0 0 30px 0; font-size:14px;
    @include breakpoint("small") {width: 46%; margin: 0 0 3% 4%; }
   }
  }
 }
 
 select{appearance: none; -webkit-appearance: none; -moz-appearance: none; background-image: url("data:image/svg+xml;charset=utf8,%3Csvg xmlns='http://www.w3.org/2000/svg' width='24' height='24' viewBox='0 0 24 24'%3E%3Cpath d='M16.59 8.59L12 13.17 7.41 8.59 6 10l6 6 6-6z' fill='rgba(117, 117, 117, 0.999)' /%3E%3C/svg%3E"); background-position: right .5rem top .6rem; background-repeat:no-repeat; background-size: 20px;}
 
 .upload-file-mn{width: $full-width; float:left;
  h3{margin-top:25px; margin-bottom:15px;}
  .upload-file-in{width: $full-width; float:left;
   .uplf-head{width: $full-width; float:left;
    .form-label{width:auto; float:none; display:inline-block; vertical-align:middle;}
	.tooltip{vertical-align:top;}
   }
   .uplf-sec{width: $full-width; float:left;
    #selected{
	  width:100%;
	  margin-bottom:17px;
	  text-overflow: ellipsis;
	  overflow:hidden;	
	  background-color: #ffffff;
	  color: $color-gray80;
	  border-radius: 5px;
	  height: 40px;
	  line-height: 40px;
	  padding:0 13px;
	  float:left;
	  border: { 
		width:1px;
		style:solid;
		color:$color-gray80;
	  }
	  font-size: 15px;
	  font-family: Amiri, Palatino-Roman, serif;
	  
	 @include breakpoint("small") {width:265px; margin-bottom:0;} 
	  
	}
	
	input[type="button"]{@include white-button; line-height:1; margin-left:10px; padding: 0.75rem 1.9rem;}
	
	.filedata{margin-left:0; line-height:normal;@include white-button; margin-bottom:0 !important;
	 @include breakpoint("small") {@include white-button; line-height:1; margin-left:10px; padding: 0.75rem 1.9rem;} 
	}
	
   }
   
  }
 }
 
#repeater{width: $full-width; float:left; margin-bottom:20px;}

.step-actions{width: $full-width !important; padding: 0 !important; flex-wrap: wrap;
 .gray-button{@include gray-button; float:left; line-height: normal; margin-bottom:0 !important; padding: 0.90rem 2.8rem;
  @include breakpoint("small") {@include gray-button; float:left; line-height: 1; font-size:15px; margin-bottom:0 !important; padding: 0.90rem 2.8rem;} 
 }
 .white-button{@include white-button; margin-right:0; margin-bottom:20px !important;
   @include breakpoint("small") {margin-right:16px; margin-bottom:0 !important;} 
 }
 .blue-button{@extend %blue-button; margin:0 !important; padding: 0.85rem 5rem !important;
  &:hover{@extend %blue-button-hover;}
 }
}

.reapter-title{width: $full-width; float:left; margin-bottom:15px;}

.com_block {width: $full-width; float:left;
 &.preo-cls{margin-bottom:40px;}
 h3{margin-top:0; margin-bottom:25px; font-size:26px;}
}

.cta_block{width: $full-width; float:left; padding-top:25px;
 p{margin-bottom:40px;}
}

.gcaptcha{width: $full-width; float:left; margin-top:15px; margin-bottom:35px;
 .g-recaptcha{transform: scale(0.93); float: left;margin-left: -11px;
  @include breakpoint("small") {transform: scale(1); float: left;margin-left: 0;} 
 }
 
 .captchaerror{color:#9a3324; margin-top: 5px; width: $full-width; float:left;} 
 
}

 
}

} 

}


ul.stepper > li:not(:last-of-type){padding-bottom: 0px !important;}
ul.stepper .step:not(:last-of-type)::after{background-color: transparent !important;}


// Credit Application [CLOSE]


// Quick Order Form [START]

.page-content--quick_order{ max-width: 1106px; margin: 0 auto;
 h1.page-heading{margin-top: 25px !important;}
 .page-content{padding:0;}
 
}

.qo_main{width: $full-width; float:left;

.qo_second_title{width: $full-width; float:left; margin-bottom:5px;
 p{text-align:$align-center;}
}

.qo_step{width: $full-width; float:left;
 ul.tabs{border-bottom: none; margin-left:-25px; text-align:$align-center; font-size:0;
  li.tab{border: none; margin-left:25px; float:none; display:inline-block; vertical-align:top;
   a.tab-title{border:$text-color-standard-gray solid 1px; border-radius:5px; width:352px; color:$color-blue; padding-top:11px; padding-bottom:11px;font-family:$font-arial; background-repeat:no-repeat;
    span{font-size:21px; font-weight:$font-weight-bold; text-transform:uppercase; display:block;}
    small{font-size:16px; display:block; font-weight:400;}
	
	&:hover{box-shadow: 0 0 0 2px #555;}
	
   }
   
  &.tab-item{
   a.tab-title{background-image:url("../img/enter-tab-icon.png"); background-position:22px center; padding-left:70px;}
  }

  &.tab-import{
   a.tab-title{background-image:url("../img/import-csv-icon.png"); background-position:25px center; padding-left:85px;}
  }	
  
  &.tab-orderhistory{
   a.tab-title{background-image:url("../img/order-tab-icon.png"); background-position:28px center; padding-left:98px;}
  }	
   
  &.is-active{
   a.tab-title{background-color:#eeeeee; border-color:#eeeeee;}
  } 
   
   
  }
 }
}


#tab-order.has-order{padding-top:0; text-align:$align-center;
 h3{text-align:$align-center; font-family:$font-palatino; font-size:21px; margin-bottom:32px; margin-top:0; line-height:30px;}
 .DateMn{width:280px; display:inline-block; vertical-align:top; float:none;
	@include breakpoint("small") {width:462px;} 
	@include breakpoint("large") {width:auto;} 
	
  .Left{width:100%; float:left; font-size:0; margin-right:0; text-align:left; margin-bottom:16px;
