variable.scss:
$screen-desktop: 1366px;
$screen-tablet: 1279px;
$screen-mobile: 599px;
$screen-standard-width: 1280px;

/*$font-palatino: Amiri, Palatino-Roman, serif;*/
$font-palatino: "Palatino Linotype", "Book Antiqua", Palatino, serif;
$font-arial: Arial, Helvetica, sans-serif;
$font-opensans: "Open Sans", Arial, Helvetica, sans-serif; 

$text-color-standard-gray: #555555;
$color-blue: #425563;
$color-blue-hover: #687782;
$color-red: #9a3324;
$color-brown: #7d5841;
$color-green: #4c6a50;
$color-white: #ffffff;
$color-gray80: #555555;
$full-width: 100%;
$base-font-size: 16px;
$align-center: center;
$align-left: left;
$font-weight-bold: 700;
$font-style-italic: italic;
$underline: underline;


%blue-button{
  font-family: Arial, Helvetica, sans-serif;
  color: $color-white;
  font-size: $base-font-size;
  font-weight: 400;
  line-height: 24px;
  letter-spacing: 0;
  text-align: center;
  background: $color-blue;
  border: 1px solid $color-blue;
  border-radius: 0.3125rem;
  padding: 0.75rem 1.5rem;
  margin: 0.5rem 1rem 0;
  display: inline-block;
  text-decoration: none;
  -webkit-transition:all .3s ease-in;
  -moz-transition:all .3s ease-in;
  -ms-transition:all .3s ease-in;
  -o-transition:all .3s ease-in; 
  transition:all .3s ease-in;

  @media (max-width: 599px) {
    width: 100%;
    margin: 0 auto 1rem;
  }

}

%blue-button-hover{background: $color-blue-hover; border: 1px solid $color-blue-hover; color: $color-white;}

@mixin gray-button{
  font-family: Arial, Helvetica, sans-serif;
  color: $color-white;
  font-size: $base-font-size;
  line-height: 24px;
  letter-spacing: 0;
  text-align: center;
  background: $text-color-standard-gray;
  border: 1px solid $text-color-standard-gray;
  border-radius: 5px;
  padding: 0.75rem 1.5rem; 
  text-decoration: none; 
  display: inline-block;
  -webkit-transition:all .3s ease-in;
  -moz-transition:all .3s ease-in;
  -ms-transition:all .3s ease-in;
  -o-transition:all .3s ease-in; 
  transition:all .3s ease-in;
  
   &:hover {
      background: $color-gray80;border-color: $color-gray80; color: $color-white;
   }
/*
   &:click{
    background: $color-gray80;border-color: $color-gray80; color: $color-white;
   }
   &:focus{
    background: $color-gray80;border-color: $color-gray80; color: $color-white;
   }
*/
   @media (max-width: 599px) {
    width: 100%;
    margin: 0 auto 1rem;
  }
  
}

@mixin white-button{
  font-family: $font-arial;
  color: $text-color-standard-gray;
  font-size: $base-font-size;
  line-height: 24px;
  letter-spacing: 0;
  text-align: center;
  background: $color-white;
  border: 1px solid $color-gray80;
  border-radius: 5px;
  padding: 0.75rem 1.5rem; 
  text-decoration: none; 
  display: inline-block;
  -webkit-transition:all .3s ease-in;
  -moz-transition:all .3s ease-in;
  -ms-transition:all .3s ease-in;
  -o-transition:all .3s ease-in; 
  transition:all .3s ease-in;
  
   &:hover {
      background: $text-color-standard-gray;border-color: $text-color-standard-gray; color: $color-white;
   }
/*   
   &:click{
    background: $text-color-standard-gray;border-color: $text-color-standard-gray; color: $color-white;
   }
   
   &:focus{
    background: $text-color-standard-gray;border-color: $text-color-standard-gray; color: $color-white;
   }
*/   
   @media (max-width: 599px) {
    width: 100%;
    margin: 0 auto 1rem;
  }

}

@mixin h1-heading{
  font-family: $font-palatino;
  color: $text-color-standard-gray;
  font-size: 40px;
  font-weight: 400;
  line-height: 55px;
  letter-spacing: 0;
  margin-top: 0px !important;
  margin-bottom: 8px !important;
  margin-top: 0px;
  margin-bottom: 8px;
    @media (max-width: 599px) {
      font-size: 32px;
      line-height: 44px;
    }
}
h1{@include h1-heading;}


@mixin h2-heading{
  font-family: $font-palatino;
  color: $text-color-standard-gray;
  font-size: 32px;
  font-weight: 400;
  line-height: 44px;
  letter-spacing: 0;
  margin-top: 36px;
  margin-bottom: 8px;
    @media (max-width: 599px) {
      font-size: 26px;
      line-height: 36px;
    }
}
h2{@include h2-heading;}


@mixin h3-heading{
  font-family: $font-palatino;
  color: $text-color-standard-gray;
  font-size: 24px;
  font-weight: 400;
  line-height: 33px;
  letter-spacing: 0;
  margin-top: 36px;
  margin-bottom: 8px;
    @media (max-width: 599px) {
      font-size: 22px;
      line-height: 30px;
    }
}
h3{@include h3-heading;}


@mixin h4-heading{
  font-family: $font-palatino;
  color: $text-color-standard-gray;
  font-size: 18px;
  font-weight: 400;
  line-height: 27px;
  letter-spacing: 0;
  margin-top: 36px;
  margin-bottom: 8px;
    @media (max-width: 599px) {
      font-size: 18px;
      line-height: 25px;
    }
}
h4{@include h4-heading;}


@mixin h5-heading{
  font-family: $font-palatino;
  color: $text-color-standard-gray;
  font-size: 16px;
  font-weight: 400;
  line-height: 24px;
  letter-spacing: 0;
  margin-top: 36px;
  margin-bottom: 8px;
    @media (max-width: 599px) {
      font-size: 16px;
      line-height: 22px;
    }
}
h5{@include h5-heading;}


@mixin paragraph{
  font-family: $font-arial;
  color: $text-color-standard-gray;
  font-size: $base-font-size;
  font-weight: 400;
  line-height: 24px;
  letter-spacing: 0;
  margin-bottom: 16px;
}
p{@include paragraph;}


@mixin blockquote{
  font-family: Filson Pro;
  color: $color-blue;
  font-size: 21px;
  font-weight: 400;
  line-height: 31.5px;
  letter-spacing: 0;
  margin-top: 16px;
  margin-bottom: 16px;
  margin-left: 40px;
  margin-right: 40px;
    @media (max-width: 599px) {
      font-size: 20px;
      line-height: 28px;
      margin-left: 20px;
      margin-right: 20px;
    }
}
blockquote{@include blockquote;}


@mixin superscript{
  font-size: 0.5rem;
  vertical-align: baseline;
  position: relative;
  top: -0.5rem;
}
sup{@include superscript;}


a{
  &:visited{
    color: $color-blue-hover;
  }
  &:hover{
    color: $color-gray80;
  }
  &:focus{
    color: $color-gray80;
  }
  &.active{
    color: $color-brown;
  }
}


.form-input{
  height: 40px;
  border-radius: 5px;
  border: 1px solid $color-gray80;
  font-size: 15px;
  color: $color-gray80;
  padding: 0 13px;
  font-family: $font-palatino;
  width: $full-width;
}
