  font-style: normal;
}

* {
  outline:none;
}
body .yotpo .yotpo-review .yotpo-header .yotpo-icon-profile{background-color: $text-color-standard-gray;}
body .yotpo .yotpo-review .yotpo-header.yotpo-verified-user .yotpo-icon-circle-checkmark{color: $color-green;}
body .yotpo .yotpo-review .yotpo-header .yotpo-header-element .y-label.yotpo-user-name{color: $text-color-standard-gray;}

.mobile-menu{display: none;}

ul.wtwo-colmn{width:560px !important;}
ul.wtwo-colmn .wtwo-colmn-f{width:50%; float: left;}
ul.wtwo-colmn .wtwo-colmn-f li{width:100% !important;}
ul.wtwo-colmn li{width:50% !important;}
ul.mbl-signuplink{margin-top: 20px !important;}
ul.mbl-signuplink li{border-bottom:none !important; font-size: 14px !important;}
ul.mbl-signuplink li a{border-bottom:none !important; font-size: 14px !important;}


body .header-logo{margin: 0 auto;}
body{font-family: arial, sans-serif; float: left; width: 100%;}
.banners,.navPages{max-width: $full-width;}
.banners p{color: #fff; font-size: 14px;}
.navPages{text-align: left;}
.banners a{text-decoration: none; color: $color-white !important;}
.banners,.header{padding: 0 30px; float: left; width: $full-width;}
.header{position: relative;}
.container{max-width: $screen-standard-width; width: $full-width; padding: 0; margin: 0 auto; }
.page-default .container{max-width: $full-width; width: $full-width; padding: 0; margin: 0 auto; }
.body {padding: 0 2.4em 2.4em 2.4em;float: left;width: $full-width;max-width: $full-width;margin: 0; box-sizing: border-box;}
.body .main.full{text-align: center;}
.heroCarousel .slick-list{width: $full-width;}
.banners {background-color: #425563; padding-top: 5px; padding-bottom: 4px; float: left; width: $full-width; font-size: 14px;}
.navPages-container{float: left; width:auto; text-align: left;}
.header-logo .header-logo-image{    position: relative;
    top: auto;
    bottom: auto;
    animation: auto;
    right: auto;
    margin: auto;
    max-height: inherit;
    width: auto;
    height: auto;
    max-height: 61px;}
.header-logo {
    margin: 0 auto;
    width: auto;
    z-index: 0;
    float: left;
    display: inline-block;
    /*max-width: 20%;*/
    top: 50%;
    bottom: auto;
    transform: translateY(-50%);
    min-height: 61px;
}
.navUser {padding: 0;position: relative;right: auto;top: auto;transform: none;z-index: 50;float: right;transform: none;width: auto;}
.home-sliders{padding: 0 2.4em;}
.banners a.log-block{text-decoration: underline; font-size: 12px;}
.slick-dots li.slick-active button::before{display: none;}
.slick-dots li.slick-active button{background-color: #555555; border: 1px solid #ffffff;}
.slick-dots li button{background-color: #fff; border: 1px solid #757575 !important;}

.navPages-action,.navUser-action {padding: 0;}
.heroCarousel {margin-top: 0;}
.header-logo-image-container::after {padding-bottom: 0; display: none;}
.header-logo > a {display: inline-block;width: auto;position: relative;top: auto;margin-top: 0;}
.header-logo{margin: 0; z-index: 0;}
.head-right {float: right;width: auto;position: relative;z-index: 9; margin-top: 0; padding: 23px 0;}
.search-div .form-field,.search-div .form{margin: 0;}
.navPages-action, .navUser-action{font-weight: 400; text-transform: capitalize; color: $text-color-standard-gray; font-size: 16px;}
.navPages-action:hover,.navPages-action.activePage{color: #5F6D77; font-weight: 700;}

.navPages-action.activePage{font-weight: 700;}
.navPages ul li{margin-right: 27px; padding: 0;}
.navPages ul li a{padding: 29px 0;}
.header.sticky .navPages ul li a {padding: 22px 0;}
.header.sticky .head-right{padding: 16px 0;}
.navPages ul li:last-child{margin-right: 0;}
.header{border-bottom: 1px solid $color-gray80; padding:0 2.4rem;}
.head-right .navUser-section .quickSearch .navUser-action--quickSearch{display: none;}
.search-div{width: 219px;}
.search-div .form-input{height: 35px; padding: 0 40px 0 13px; border-color: $color-gray80; border-radius: 5px; width: $full-width; color: $color-gray80; font-family: $font-palatino;}
body select,body .form-input{color: $text-color-standard-gray !important;}
.search-div .btn {background: url(../img/search-btn-img.png) no-repeat center center;position: absolute;right: 12px;text-indent: -9999px;box-shadow: none;border: 0;
  width: 17px;height: 35px; top:0;}
.search-div .form-field{position: relative;}
.footer {float: left;width: $full-width; border: 0; padding: 0}

.footer-bottom .footer-info-locator .form-text::-webkit-input-placeholder,.search-div .form-input::-webkit-input-placeholder { /* Chrome/Opera/Safari */
  opacity: 1 !important; color: $text-color-standard-gray !important;
}
.footer-bottom .footer-info-locator .form-text::-webkit-input-placeholder, .search-div .form-input::-moz-placeholder { /* Firefox 19+ */
 opacity: 1 !important; color: $text-color-standard-gray !important;
}
.footer-bottom .footer-info-locator .form-text::-webkit-input-placeholder, .search-div .form-input:-ms-input-placeholder { /* IE 10+ */
  opacity: 1 !important; color: $text-color-standard-gray !important;
}
.footer-bottom .footer-info-locator .form-text::-webkit-input-placeholder, .search-div .form-input:-moz-placeholder { /* Firefox 18- */
  opacity: 1 !important; color: $text-color-standard-gray !important;
}

::-webkit-input-placeholder { /* Chrome/Opera/Safari */
  opacity: 1 !important; color: $text-color-standard-gray !important;
}
::-moz-placeholder { /* Firefox 19+ */
 opacity: 1 !important; color: $text-color-standard-gray !important;
}
:-ms-input-placeholder { /* IE 10+ */
  opacity: 1 !important; color: $text-color-standard-gray !important;
}
:-moz-placeholder { /* Firefox 18- */
  opacity: 1 !important; color: $text-color-standard-gray !important;
}

.navUser-item--account .signin-icon{float: left;background: url(../img/account-icn.png) left center no-repeat; font-size: 13px; line-height: 20px; padding-left: 21px; margin-left: 13px; position: relative;}
.account-dropdown{position: absolute; right: 0; top: $full-width; width: 162px; display: none; display: none; padding-top: 12px;}
.navUser-item--account .signin-icon:hover .account-dropdown{display: block;}
.account-dropdown ul{position: relative; padding: 5px 0;box-shadow: 0px 0px 7px 0 rgba(0, 0, 0, 0.2);border: 1px solid $color-gray80; background: #fff; border-radius: 5px;}
.account-dropdown ul::after, .account-dropdown ul::before {content: '';display: block;position: absolute;left: 68%;width: 0;height: 0;border-style: solid;transform: rotate(-90deg);  margin-top: -20px;}
.signin-icon .account-link{cursor: pointer;}
.navUser-item--account .signin-icon.active{background: url(../img/account-icn-active.png) left center no-repeat;}
.signin-icon.active .account-link{color: #5F6D77}
.account-dropdown ul li{width: $full-width;}
.account-dropdown ul li a{font-size: 15px; padding: 6px 19px; width: $full-width;}
.navUser-item--account .signin-icon .account-dropdown ul li a:hover{background: $color-gray80; color: #fff;}

/* this border color controlls the color of the triangle (what looks like the fill of the triangle) */
.account-dropdown ul:after {
    top: 0;
    border-color: transparent transparent transparent #fff;
    border-width: 10px;
}

/* this border color controlls the outside, thin border */
.account-dropdown ul::before {
  top: -1px;
  border-color: transparent transparent transparent $color-gray80;
  border-width: 10px;
  right: 0;
}
.navUser-item--account .signin-icon .account-dropdown a{font-size: 15px; color: $text-color-standard-gray; font-weight: 400;}
.navUser-item--account .signin-icon .account-dropdown a:hover{text-decoration:none;}
.navUser-item--account .signin-icon a{font-size: 13px; color: $text-color-standard-gray; line-height: 20px; font-weight:700;}
.navUser-item--account .signin-icon a:hover{color: $color-brown; text-decoration:underline;}
.navUser-or{margin: 0; color: $color-gray80; padding: 0;}
.navUser-item--account .navUser-or + .navUser-action {margin-right: 0;}
.navUser ul li{display: inline-block;vertical-align: middle;float: none;}
.navUser ul li.navUser-item--cart a.navUser-action{background: url(../img/cart-icn.png) no-repeat left center; padding-left: 20px; margin-left: 20px;}
.navUser ul li.navUser-item--cart .countPill{display: inline-block; background: transparent; color: #56564f; font-size: 13px; width: auto; height: auto;}
.navUser-item-cartLabel{display: none;}

body .navPages-container{position: static;}

.navPages-action.is-open {background-color: transparent;}
.navPages-item.last-category .navPages-action{color: $color-brown}
.navPages div.navPage-subMenu{padding: 0 0 0 12px!important; background-color: rgba(0,0,0,0.5); margin-top: 1px; left: 0;width: 100%;top: $full-width; /*height:calc(100vh - 113px);height: calc(100vh + 100px) !important;*/

-webkit-transition: opacity .3s ease-in-out; 
-moz-transition: opacity .3s ease-in-out; 
-ms-transition: opacity .3s ease-in-out; 
-o-transition: opacity .3s ease-in-out; 
transition: opacity .3s ease-in-out;  
}
.navPages div.navPage-subMenu ul.wtwo-colmn li:hover ul {
    left: 558px !important;
}
.navPages div.navPage-subMenu ul.wtwo-colmn ul li.navPage-subMenu-item-child {
    width: 100% !important;
}
.header.sticky .navPages div.navPage-subMenu{height: 100vh;}
.navPages div.navPage-subMenu ul {float: left;width: 240px;height: 100%; background: #fff;margin: 0;display: -ms-flexbox;display: flex;-ms-flex-wrap: wrap;flex-wrap: wrap;
  padding: 12px 0 58px 0;align-items: flex-start;align-content: baseline; /*display: none;*/ opacity: 0;}
  
.navPages div.navPage-subMenu ul ul{left: -1200px; top: 0; position: absolute; height: $full-width; background: #eeeeee; border-left: 1px solid #fff; 
-webkit-transition: opacity .5s ease-in-out; 
-moz-transition: opacity .5s ease-in-out; 
-ms-transition: opacity .5s ease-in-out; 
-o-transition: opacity .5s ease-in-out; 
transition: opacity .5s ease-in-out;
visibility: hidden;
backface-visibility: hidden;
    
}

.navPages div.navPage-subMenu ul li:hover ul{left: 252px; width: 253px;}

.navPages div.navPage-subMenu ul ul ul{left: -1200px;}
.navPages div.navPage-subMenu ul ul ul ul{left: -1600px; visibility: hidden; backface-visibility: hidden;}

.navPages div.navPage-subMenu ul ul li:hover ul{left: 252px;}

.navPages div.navPage-subMenu ul ul ul li:hover ul{left: 252px; width: 253px; visibility: visible;}



.navPages div.navPage-subMenu ul li {float: left;width: $full-width;margin: 0 0 5px 0;padding: 0 15px !important;}
.navPages div.navPage-subMenu ul li a{padding: 4px 0 !important; margin: 0; border-bottom: 1px solid transparent; position: relative; display: inline-block; text-transform: none;}
.navPages div.navPage-subMenu ul li:hover > a{border-color: $color-gray80; font-weight: 700; color: $text-color-standard-gray;}
.navPages ul li a:visited{color: $text-color-standard-gray;}
.navPages div.navPage-subMenu ul li > a:hover{background-color: transparent; color: $text-color-standard-gray; font-weight: 700; opacity: 1;}
/*.navPages ul.navPages-list > li.navPages-item:hover > a + div.navPage-subMenu {/*display: block;*//* opacity: 1;}*/
.navPages ul.navPages-list > li.navPages-item > a{position: relative; z-index: 9;}
/* this border color controlls the outside, thin border */
.navPages ul.navPages-list > li.navPages-item > a.has-subMenu::before {bottom: -21px;border-color: transparent transparent transparent $text-color-standard-gray;border-width: 10px;right: 0;}
.navPages ul.navPages-list > li.navPages-item > a.has-subMenu::after {bottom: -20px;border-color: transparent transparent transparent #fff;border-width: 10px;}
.navPages ul.navPages-list > li.navPages-item > a + div.navPage-subMenu.is-open {/*display: block;*/ opacity: 1;}

.navPages div.navPage-subMenu ul li.quick-order-menu{border: 0; margin-top: 4px;}
.navPages div.navPage-subMenu ul li.quick-order-menu a{width: 174px; display: inline-block; line-height: 44px; font-size: 15px; font-weight: 700; color: $text-color-standard-gray; border: 1px solid $text-color-standard-gray; border-radius: 5px;background-image: url(../img/quick-order.png); background-repeat: no-repeat; background-position: left 11px center; padding-left: 37px !important;}
.navPages div.navPage-subMenu ul li.quick-order-menu a:hover{border: 1px solid $text-color-standard-gray; opacity: 0.7;}
.header.sticky{position: fixed;width: $full-width;z-index: 99;top: 0;margin: 0;-webkit-animation: fadeInDown 1s both;animation: fadeInDown 1s both;}
@-webkit-keyframes fadeInDown{0%{opacity:0;-webkit-transform:translateY(-20px);transform:translateY(-20px)}100%{opacity:1;-webkit-transform:translateY(0);transform:translateY(0)}}@keyframes fadeInDown{0%{opacity:0;-webkit-transform:translateY(-20px);-ms-transform:translateY(-20px);transform:translateY(-20px)}100%{opacity:1;-webkit-transform:translateY(0);-ms-transform:translateY(0);transform:translateY(0)}}
.home-slider{display: none;}
.home-sliders{float: left; width: $full-width;}
.slider{float: left; width: $full-width; margin: 0; list-style: none;}
.slider .item{float: left; width: $full-width; background-position: top center; background-repeat: no-repeat; height: 450px; background-size: cover !important; position: relative;}
.slider .item a{float: left; width: 100%; height: 100%; text-decoration: none;}
.slider .content{position: absolute; right: 0; top: 50%; transform: translateY(-50%); text-align: left; max-width: 50%; padding: 38px 74px; float: left; width: 100%;}
.slider .item.even .content{left: 0; right: auto;}
.slider .content h1{
  @include h1-heading;
  font-size: 57.6px; line-height: 79px; text-transform: capitalize; margin: 0 0 5px 0;}
.slider .content p{font-size: 26.4px; line-height: 44px; color: $text-color-standard-gray; margin-bottom: 26px;
font-family: "Palatino Linotype","Book Antiqua",Palatino,serif;}
.slider .content .gray-button{
  @include gray-button;
  font-size: 19px; 
  width: 203px; 
  line-height: 29px;
}
.home-card-section{float: left; width: $full-width; margin: 2.4em 0;}
.home-card-section ul{margin: 0 -1.2rem; list-style: none; display: flex;}
.home-card-section ul li{float: left; width: 33.33%; margin: 0 1.2rem;background: #eee;}
/*.home-card-section ul li figure{height: 463px; width: $full-width; background-position: center center; background-repeat: no-repeat; background-size: cover; margin: 0;}*/
.home-card-section ul li figure a{float: left; width: $full-width; height: $full-width; }
.home-card-section ul li figure{width: $full-width; background-position: center center; background-repeat: no-repeat; background-size: cover; margin: 0; background: none !important; overflow: hidden;}
.home-card-section ul li figure img{-webkit-transition:-webkit-transform .6s linear;-ms-transition:-ms-transform .6s linear;transition:transform .6s linear;}
.home-card-section ul li:hover figure img{transform: scale(1.2);}
.home-card-section ul li .content{background: #eeeeee; float: left; width: $full-width; padding: 2.4rem; text-align: left;}
.home-card-section ul li h3{ font-size: 36px; line-height: 49px;margin: 0 0 4px 0;}
.home-card-section ul li h3 a{color: $text-color-standard-gray; text-decoration: none;}
.home-card-section ul li:hover h3 a{color: #5F6D77}
.home-card-section ul li p{font-size: 19px; color: $text-color-standard-gray; line-height: 33px;}
.home-card-section ul li .gray-button {
  @include gray-button;
  background-color: #eeeeee;color:$text-color-standard-gray;font-size: 19px;line-height: 28px;padding: 0.75rem 1.9rem;}
.home-card-section ul li:hover .gray-button{background-color: $text-color-standard-gray; color:$color-white;}

.home-trending{float: left; width: $full-width;}
.home-trending .productCarousel{padding: 25px 25px 63px 25px; margin: 0; box-sizing: border-box;}
.home-trending .slick-prev {left: 0;padding: 0;background-color: transparent;border: 0;width: auto;}
.home-trending .slick-next {right: 0; padding: 0; background-color: transparent;border: 0;width: auto;}
.home-trending .productCarousel-slide {padding: 0 17px;}
.home-trending h2{margin: 0; font-size: 50px; color: $text-color-standard-gray; line-height: 1.375; border-bottom: 1px solid $color-gray80; padding-bottom: 21px;}
.home-trending .card {float: left; width: $full-width;}
.home-trending .card-figure{float: left; width: $full-width;}
.home-trending .card-figure a{display: table; table-layout: fixed; vertical-align: middle; float: left; width: $full-width; text-align: center; background: #fff;}
.home-trending .card-figure a .card-img-container{display: table-cell; width: $full-width; vertical-align: middle;}
.home-trending .card-figure a .card-img-container img{display: inline-block; vertical-align: middle; width: auto; position: relative; max-height: $full-width; max-width: $full-width;}
.home-trending .card-figure a .card-img-container::after{padding: 0; display: none;}
.home-trending .card-body{float: left; width: $full-width; text-align: center; padding-top: 25px;}
.home-trending .card-body h4{font-size: 19px; font-weight: 700; line-height: 31px;font-family: arial, sans-serif;}
.home-trending .card-body h4 a:hover,.home-trending .card-body h4 a:visited,.home-trending .card-body h4 a:focus{color: $color-brown;}
.home-trending .card-body .prd-sku{display: none;}
.home-trending .card-text .price{font-size: 19px; color: $text-color-standard-gray; line-height: 31px;}
.home-trending .slick-dots{bottom: 0;}
.home-trending .slick-dots li{height: 16px;width: 16px;margin: 0;padding: 0; margin-right: 22px;}
.home-trending .slick-dots li:last-child{margin-right: 0}
.home-trending .slick-dots li button{height: $full-width; width: $full-width; border-radius: $full-width; border: 1px solid #56564f;}
.home-trending .slick-dots li button.active{background: #56564f;}

.about-section{float: left; width: $full-width; background: #eeeeee; padding: 2.4rem;margin-top: 38px;}
.about-section h2{font-size: 50px; line-height: 63px; color: $text-color-standard-gray; margin: 0 0 9px 0}
.about-section p{font-size: 19px; color: $text-color-standard-gray; line-height: 28px; margin: 0 0 3px 0;}
.about-section #aboutsection a{font-size: 19px; text-decoration: underline; line-height: 28px; color: #4D4F56 !important;}
.about-section a:hover{color: $color-brown;}
.home-card-section.home-blog-section{margin-bottom: 0;}
.about-section.retailer-sec{background: transparent; padding: 0}
.about-section.retailer-sec a{
  @include gray-button;
  color: #fff; font-size: 19px; text-decoration: none;}
.about-section.retailer-sec p { margin: 0 0 29px 0;}
.about-section.retailer-sec h2 {margin: 0 0 13px 0;}

.quicklink-bar{float: left; width: $full-width; padding:0 2.4rem; background: #eeeeee;}
.quicklink-bar ul{float: left; width: $full-width; margin: 0; padding: 10px 0; list-style: none;}
.quicklink-bar ul li{float: left; width: 25%; text-align: center; margin: 0}
.quicklink-bar ul li a{text-decoration: none; font-size: 17px; font-weight: 700; color: $text-color-standard-gray; line-height: 24px; display: inline-block; padding-left: 32px; background-position: left center; background-repeat: no-repeat;}
.footer-bottom{float: left; width: $full-width;padding:0 2.4rem;}
.quicklink-bar ul li.quick a{background-image: url(../img/quick-order.png);}
.quicklink-bar ul li.order a{background-image: url(../img/shipping-icon.png); padding-left: 43px;}
.quicklink-bar ul li.invoice a{background-image: url(../img/invoice.png);}
.quicklink-bar ul li.account a{background-image: url(../img/ft-person.png);}

.footer-bottom{float: left; width: $full-width;}
footer .container{width: 100%; max-width: 100%;}
.footer-bottom .container{padding: 23px 0 0 0;}
.footer-bottom .footer-info-col {width: 25%; padding: 0 12px; margin: 0}
.footer-bottom .footer-info {margin: 0;}
.footer-bottom .footer-info-locator p span{display: block;}
.footer-bottom .footer-info-col h5{text-transform: uppercase; color: #29546b; font-weight: 700; line-height: 31px; float: left; width: $full-width; margin: 0 0 7px 0; font-size: 17px;font-family: arial, sans-serif;}
.footer-bottom .footer-info-col p{font-size: 15px; color: $text-color-standard-gray; line-height: 22px; float: left; width: $full-width; margin: 0 0 16px 0}
.footer-bottom .footer-info-locator .form-input{max-width: 267px;}
.footer-bottom .footer-info-locator .gray-button{
  @include gray-button;
  width: 135px; height: 40px; padding: 0; font-size: 15px; margin-top: 10px; display: block;}
.footer-bottom .footer-info-col ul{float: left; width: $full-width; padding: 0; margin: -3px 0 0 0;}
.footer-bottom .footer-info-col ul li{float: left; width: $full-width;}
.footer-bottom .footer-info-col ul li a{font-size: 15px; text-transform: capitalize; line-height: 23px; color: $text-color-standard-gray; text-decoration: none;}
.footer-bottom .footer-info-col ul li a:hover{color: $color-brown}
.footer-bottom .phone-div{float: left; width: $full-width; background-image: url(../img/phone-icn.png); background-repeat: no-repeat; background-position: left top 5px; padding-left: 30px; margin-bottom: 12px;}
.footer-bottom .phone-div p{line-height: 20px; margin-bottom: 0}
.footer-bottom .phone-div p a{color: $text-color-standard-gray; text-decoration: none;}
.footer-bottom .phone-div p a:hover{color: $color-brown}
.footer-bottom .email-div{float: left; width: $full-width; background-image: url(../img/mail.png); background-repeat: no-repeat; background-position: left center; padding-left: 30px;}
.footer-bottom .email-div p a{color: $text-color-standard-gray; text-decoration: none;}
.footer-bottom .email-div p a:hover{color: $color-brown}
.footer-bottom .email-div p{line-height: 20px; margin-bottom: 0}
.footer-bottom .phone-div p span{display: block;}
.footer-bottom .newsletter-div{float: left; width: $full-width; margin-top: 33px;}
.footer-bottom .newsletter-div p{margin: 0 0 10px 0;}
.footer-bottom .newsletter-div form{float: left; width: 100%;}
.footer-bottom .newsletter-div .form-input{max-width: 208px; width: $full-width; margin-right: 11px;}
.footer-bottom .newsletter-div .gray-button{
