.custom_Nav
{	text-align: left; max-width: 100%; font-size: 0;
}
.custom_Nav ul 
{   float: none; list-style: none; display: inline-block; margin: 0;
}
.custom_Nav ul ul 
{   display: none;
}
.custom_Nav ul > li > a 
{   position: relative; z-index: 9;
}
.custom_Nav ul li a 
{   float: left; padding: 29px 0; font-weight: 400; text-transform: capitalize; color: #555; font-size: 16px; display: inline-block;
    text-decoration: none;
}
.custom_Nav ul li 
{   display: inline-block; margin-right: 27px; padding: 0; float: left;
}
.Nav_main 
{   padding: 0 0 0 12px!important; background-color: transparent; margin-top: 1px; display: block; left: 0; width: auto; top: 100%;
}
.custom_Nav > ul > li > ul
{   padding: 0 0 0 12px!important;  background-color: transparent;  margin-top: 1px; left: 0; width: auto; top: 100%; position: absolute;
    text-align: left; 
}
.custom_Nav>ul>li:hover>ul 
{   display: block!important; float: left; width: 240px; background: #fff; margin: 0; -ms-flex-wrap: wrap; flex-wrap: wrap;
    padding: 12px 0 58px; -ms-flex-align: start; align-items: flex-start; -ms-flex-line-pack: baseline;
    align-content: baseline; display: none;
}
.custom_Nav>ul>li:hover>ul>li>a 
{   padding: 2px 0!important; margin: 0; border-bottom: 1px solid transparent; position: relative; display: inline-block; width: 100%;
    float: left;
}
.custom_Nav .navPages-action-moreIcon 
{   display: block; position: absolute;  right: 0; top: 50%; transform: translateY(-50%) rotate(-90deg); margin: 0;
}
.custom_Nav svg 
{   fill: #333; stroke: #333;
}
.custom_Nav>ul>li:hover>ul>li>a:visited 
{   color: #5F6D77; 
}
.custom_Nav>ul>li:hover>ul>li 
{   float: left; width: 100%; margin: 0 0 5px; padding: 0 15px!important;
}
.custom_Nav > ul > li > a:hover::after {
    bottom: -20px;
    border-color: transparent transparent transparent #fff;
    border-width: 10px;
}
@media only screen and (min-width: 1280px)
{
	.custom_Nav > ul > li > a:hover::after, .custom_Nav > ul > li > a:hover::before {
		content: '';
		display: block;
		position: absolute;
		left: 50%;
		width: 0;
		height: 0;
		border-style: solid;
		transform: rotate(90deg);
		margin-top: 0;
		margin-left: -10px;
		visibility: hidden;
	}
	.custom_Nav > ul > li > a:hover::after, .custom_Nav > ul > li > a:hover::before  
	{   visibility: visible;
	}
}

