#checkout-app .form-check-main .form-check:first-child.ActiveRadio {border-top-left-radius: 4px; border-top-right-radius: 4px; border-bottom-left-radius: 0; border-bottom-right-radius: 0; border-top:none; border-bottom:#555 solid 1px;}
#checkout-app .form-check-main .form-check .form-check-label{color: #555;font-size: 16px;}
#checkout-app .form-check-main .form-check .form-check-input{margin-right: 10px;}

#checkout-app .form-check-main .form-check #DeliveryDate-fieldset{margin-left: 25px;margin-top: 5px;}
#checkout-app .form-check-main .form-check #DeliveryDate-fieldset .form-field{width:250px;}
.ui-state-active{border: 1px solid #555 !important; background: #555 !important;}

/*#checkout-app .layout-main .checkout-steps .vcard p.fn.address-entry,*/
#checkout-app .layout-main .checkout-steps .vcard p.address-entry span.tel,
#checkout-app .layout-main .checkout-steps .checkout-step--shipping .checkout-view-header .stepHeader-body .staticConsignment .vcard.checkout-address--static + div .shippingOption.shippingOption--alt,
#checkout-app .layout-cart section.cart-section.optimizedCheckout-orderSummary-cartSection div[data-test="cart-shipping"],
#checkout-app .layout-cart section.cart-section.optimizedCheckout-orderSummary-cartSection div[data-test="cart-taxes"],
#checkout-app .layout-main .checkout-steps .checkout-step--shipping #shippingAddresses .dropdown--select .dropdownMenu .dropdown-menu > li:first-of-type, 
#checkout-app .layout-main .checkout-steps .checkout-step--billing #billingAddresses .dropdown--select .dropdownMenu .dropdown-menu > li:first-of-type,
.ReactModalPortal .modalOverlay--afterOpen .modal-body.cart-modal-body.optimizedCheckout-orderSummary section.cart-section.optimizedCheckout-orderSummary-cartSection div[data-test="cart-shipping"],
.ReactModalPortal .modalOverlay--afterOpen .modal-body.cart-modal-body.optimizedCheckout-orderSummary section.cart-section.optimizedCheckout-orderSummary-cartSection div[data-test="cart-taxes"] {
    display: none;
}

@media only screen and (max-width: 968px) {
.layout-main{width: 100%;}
.layout{max-width: 1136px; padding-left: 1.15385rem; padding-right: 1.15385rem;}
.layout-cart{width: 100%;}

.layout-main .redeemable-payments{display:none;}

.modal {border:#888888 solid 1px;}
.cartDrawer{margin-left:0 !important; margin-right:0 !important;}
.cartDrawer h3.cartDrawer-items{font-family:$font-arial; margin-bottom:3px;}
.cartDrawer .cartDrawer-body a{font-family:$font-arial; color: #547689; font-size: 14px; text-decoration:underline;}
.cartDrawer .cartDrawer-actions h3.cartDrawer-total{font-family:$font-arial; color: #425563; font-size: 24px; font-weight:700;}
.cartDrawer-figure--stack::after{border:none;}
.cartDrawer-figure{border:none;}

.cart-modal-header{background-color:#eeeeee; padding:10px 20px; box-shadow: none;}
.cart-modal-header .cart-modal-title{font-size:24px; }
.cart-modal-header .cart-modal-link{font-family:$font-arial; color: #547689; font-size: 16px; text-decoration:underline;}

.cart-modal-header .cart-modal-close .icon{height: 2.5rem; width: 2.5rem;}
.cart-modal-header .cart-modal-close .icon svg{fill: #555555;}

.cart-modal-body{padding-left:0; padding-right:0;}
.cart-modal-body{font-size:16px;}

.cart-modal-body .optimizedCheckout-orderSummary-cartSection{
 &:nth-child(3n){border-bottom: none; background-color:#eeeeee; border-bottom-left-radius: 4px; border-bottom-right-radius: 4px;}
}

}

@media only screen and (max-width: 599px) {
.stepHeader {padding: 0.4rem 0; display: flex; flex-wrap: wrap;}
.stepHeader-body{order: 3; width: 100%; margin-left: 4.19231rem; hyphens: auto; max-width: 100%; overflow: hidden;}
.stepHeader-actions {align-self: flex-start;margin-left: auto;margin-top: 0.38462rem;}
.stepHeader-figure {margin-right: 0rem;	width: 100%;	max-width: 250px; align-items: center; display: flex; height: 2.69231rem;}

.cartDrawer .cartDrawer-body{padding-left: 5px;}

.cart-modal-header .cart-modal-link{background: url(../img/edit-icon.png) no-repeat center center; width:24px; height:24px;font-size:0; background-size: 20px;}
.cart-modal-header .cart-modal-title{width: 82%;}
.cart-modal-body .form-prefixPostfix{display:inline;}
.cart-modal-body #applyRedeemableButton{width:100%; line-height: 24px;}
.cart-modal-body .optimizedCheckout-form-input{margin-bottom:11px;}
.cart-modal-body .product-figure{margin-bottom:10px;}

.stepHeader-actions .optimizedCheckout-buttonSecondary{background: url(../img/edit-icon.png) no-repeat center center; background-size: 20px; width:24px; height:24px;font-size:0 !important; padding:0 !important; border:none;}

.stepHeader .stepHeader-title{font-size:32px;}
.stepHeader-body{margin-top:18px;}


.SelectAdd > .form-label > span{width: 180px; display: inline-block;vertical-align: bottom;}

.SelectAdd .tooltip{left: -110px;}
.SelectAdd small{margin-top: 2rem;}
.SelectAdd .tooltip .tooltiptext{width: 160px; margin-left: -80px;}

.customterms{
 .form-label{
  span{width:164px; display:inline-block; vertical-align:top;}
 }
}


.cart-priceItem--total .cart-priceItem-label{font-size:22px;}
.cart-priceItem--total .cart-priceItem-value{font-size:22px;}

.optimizedCheckout-orderSummary .LastInfo{padding: 25px 0 20px;}
.optimizedCheckout-orderSummary .LastInfo h3 span{display:block; margin-bottom: 10px;}

.oc_page_custom .sao_btn a{width:100%;}

}

@media only screen and (max-width: 550px) {
.customterms{padding:0 0 24px 50px;}
#checkout-app .form-check-main .form-check #DeliveryDate-fieldset .form-field {width: 210px;}
.modal.is-active, .modal.modal--afterOpen {
    transform: scale(1) translateY(-50%);
}

}

#checkout-shipping-options {
	display:none;
}
fieldset#checkoutShippingAddress + .form-body {
    display: none;
}
.paymentMethod {
	padding:inherit; width:inherit;
}
#shippingAddresses + div,
#billingAddresses + div,
#checkoutShippingAddress .checkout-address {
    display: none;
}


// Custom CSS [CLOSE]

variable.scss:
$screen-desktop: 1366px;
$screen-tablet: 1279px;
$screen-mobile: 599px;
$screen-standard-width: 1280px;

/*$font-palatino: Amiri, Palatino-Roman, serif;*/
$font-palatino: "Palatino Linotype", "Book Antiqua", Palatino, serif;
$font-arial: Arial, Helvetica, sans-serif;
$font-opensans: "Open Sans", Arial, Helvetica, sans-serif; 

$text-color-standard-gray: #555555;
$color-blue: #425563;
$color-blue-hover: #687782;
$color-red: #9a3324;
$color-brown: #7d5841;
$color-green: #4c6a50;
$color-white: #ffffff;
$color-gray80: #555555;
$full-width: 100%;
$base-font-size: 16px;
$align-center: center;
$align-left: left;
$font-weight-bold: 700;
$font-style-italic: italic;
$underline: underline;


%blue-button{
  font-family: Arial, Helvetica, sans-serif;
  color: $color-white;
  font-size: $base-font-size;
  font-weight: 400;
  line-height: 24px;
  letter-spacing: 0;
  text-align: center;
  background: $color-blue;
  border: 1px solid $color-blue;
  border-radius: 0.3125rem;
  padding: 0.75rem 1.5rem;
  margin: 0.5rem 1rem 0;
  display: inline-block;
  text-decoration: none;
  -webkit-transition:all .3s ease-in;
  -moz-transition:all .3s ease-in;
  -ms-transition:all .3s ease-in;
  -o-transition:all .3s ease-in; 
  transition:all .3s ease-in;

  @media (max-width: 599px) {
    width: 100%;
    margin: 0 auto 1rem;
  }

}

%blue-button-hover{background: $color-blue-hover; border: 1px solid $color-blue-hover; color: $color-white;}

@mixin gray-button{
  font-family: Arial, Helvetica, sans-serif;
  color: $color-white;
  font-size: $base-font-size;
  line-height: 24px;
  letter-spacing: 0;
  text-align: center;
  background: $text-color-standard-gray;
  border: 1px solid $text-color-standard-gray;
  border-radius: 5px;
  padding: 0.75rem 1.5rem; 
  text-decoration: none; 
  display: inline-block;
  -webkit-transition:all .3s ease-in;
  -moz-transition:all .3s ease-in;
  -ms-transition:all .3s ease-in;
  -o-transition:all .3s ease-in; 
  transition:all .3s ease-in;
  
   &:hover {
      background: $color-gray80;border-color: $color-gray80; color: $color-white;
   }
/*
   &:click{
    background: $color-gray80;border-color: $color-gray80; color: $color-white;
   }
   &:focus{
    background: $color-gray80;border-color: $color-gray80; color: $color-white;
   }
*/
   @media (max-width: 599px) {
    width: 100%;
    margin: 0 auto 1rem;
  }
  
}

@mixin white-button{
  font-family: $font-arial;
  color: $text-color-standard-gray;
  font-size: $base-font-size;
  line-height: 24px;
  letter-spacing: 0;
  text-align: center;
  background: $color-white;
  border: 1px solid $color-gray80;
  border-radius: 5px;
  padding: 0.75rem 1.5rem; 
  text-decoration: none; 
  display: inline-block;
  -webkit-transition:all .3s ease-in;
  -moz-transition:all .3s ease-in;
  -ms-transition:all .3s ease-in;
  -o-transition:all .3s ease-in; 
  transition:all .3s ease-in;
  
   &:hover {
      background: $text-color-standard-gray;border-color: $text-color-standard-gray; color: $color-white;
   }
/*   
   &:click{
    background: $text-color-standard-gray;border-color: $text-color-standard-gray; color: $color-white;
   }
   
   &:focus{
    background: $text-color-standard-gray;border-color: $text-color-standard-gray; color: $color-white;
   }
*/   
   @media (max-width: 599px) {
    width: 100%;
    margin: 0 auto 1rem;
  }

}

@mixin h1-heading{
  font-family: $font-palatino;
  color: $text-color-standard-gray;
  font-size: 40px;
  font-weight: 400;
  line-height: 55px;
  letter-spacing: 0;
  margin-top: 0px !important;
  margin-bottom: 8px !important;
  margin-top: 0px;
  margin-bottom: 8px;
    @media (max-width: 599px) {
      font-size: 32px;
      line-height: 44px;
    }
}
h1{@include h1-heading;}


@mixin h2-heading{
  font-family: $font-palatino;
  color: $text-color-standard-gray;
  font-size: 32px;
  font-weight: 400;
  line-height: 44px;
  letter-spacing: 0;
  margin-top: 36px;
  margin-bottom: 8px;
    @media (max-width: 599px) {
      font-size: 26px;
      line-height: 36px;
    }
}
h2{@include h2-heading;}


@mixin h3-heading{
  font-family: $font-palatino;
  color: $text-color-standard-gray;
  font-size: 24px;
  font-weight: 400;
  line-height: 33px;
  letter-spacing: 0;
  margin-top: 36px;
  margin-bottom: 8px;
    @media (max-width: 599px) {
      font-size: 22px;
      line-height: 30px;
    }
}
h3{@include h3-heading;}


@mixin h4-heading{
  font-family: $font-palatino;
  color: $text-color-standard-gray;
  font-size: 18px;
  font-weight: 400;
  line-height: 27px;
  letter-spacing: 0;
  margin-top: 36px;
  margin-bottom: 8px;
    @media (max-width: 599px) {
      font-size: 18px;
      line-height: 25px;
    }
}
h4{@include h4-heading;}


@mixin h5-heading{
  font-family: $font-palatino;
  color: $text-color-standard-gray;
  font-size: 16px;
  font-weight: 400;
  line-height: 24px;
  letter-spacing: 0;
  margin-top: 36px;
  margin-bottom: 8px;
    @media (max-width: 599px) {
      font-size: 16px;
      line-height: 22px;
    }
}
h5{@include h5-heading;}


@mixin paragraph{
  font-family: $font-arial;
  color: $text-color-standard-gray;
  font-size: $base-font-size;
  font-weight: 400;
  line-height: 24px;
  letter-spacing: 0;
  margin-bottom: 16px;
}
p{@include paragraph;}


@mixin blockquote{
  font-family: Filson Pro;
  color: $color-blue;
  font-size: 21px;
  font-weight: 400;
  line-height: 31.5px;
  letter-spacing: 0;
  margin-top: 16px;
  margin-bottom: 16px;
  margin-left: 40px;
  margin-right: 40px;
    @media (max-width: 599px) {
      font-size: 20px;
      line-height: 28px;
      margin-left: 20px;
      margin-right: 20px;
    }
}
blockquote{@include blockquote;}


@mixin superscript{
  font-size: 0.5rem;
  vertical-align: baseline;
  position: relative;
  top: -0.5rem;
}
sup{@include superscript;}


a{
  &:visited{
    color: $color-blue-hover;
  }
  &:hover{
    color: $color-gray80;
  }
  &:focus{
    color: $color-gray80;
  }
  &.active{
    color: $color-brown;
  }
}


.form-input{
  height: 40px;
  border-radius: 5px;
  border: 1px solid $color-gray80;
  font-size: 15px;
  color: $color-gray80;
  padding: 0 13px;
  font-family: $font-palatino;
  width: $full-width;
}
